mapper.select:
SELECT COUNT(*)
FROM CLASS_LATE L
JOIN CLASS C ON(C.CLASS_NO = L.CLASS_NO)
JOIN MEMBER M ON(M.MEMBER_NO = C.MEMBER_NO)
WHERE CLASS_NM = #{key}

	AND M.MEMBER_NO = #{memberNo}

SELECT CLASS_LATE_NO, CLASS_LATE_CONTENT, CLASS_LATE_LIKE, L.CLASS_NO, L.MEMBER_NO , CLASS_NM, MEMBER_NAME FROM CLASS_LATE L JOIN CLASS C ON(C.CLASS_NO = L.CLASS_NO) JOIN MEMBER M ON(M.MEMBER_NO = C.MEMBER_NO) WHERE CLASS_NM = #{key}
AND M.MEMBER_NO = #{memberNo}
ORDER BY 1 DESC
